import axios from "axios";

export function postTask(payload) {
  return async function (dispatch) {
    console.log("estoy en la action");
    const task = await axios.post("http://localhost:3001/task", payload);

    return dispatch({
      type: "POST_TASKS",
      payload: task.data,
    });
  };
}

export default postTask;
