import React, { useState } from "react";
import css from "./ModalSignup.module.css";
import { useDispatch, useSelector } from "react-redux";
import createUser from "../../REDUX/Actions/createUser.js";




export default function ModalSignup() {
  const dispatch = useDispatch();
  const isModalOpen = useSelector((state) => state.isOpen);


  const handleSubmit = (e) => {
    e.preventDefault();

    const { user, email, password } = e.target;
    const userData = {
      user: user.value,
      email: email.value,
      password: password.value
    };

    if (userData.user === "" || userData.email === "" || userData.password === "") {
      return alert("You must complete all fields");
    }

    dispatch(createUser(userData))
      .then(() => {
        // Usuario creado exitosamente
        alert("User created successfully. Please sign");
      })
      .catch((error) => {
        // Error en la creación del usuario
        if (error.response && error.response.status === 409) {
          // Si el estado de respuesta es 409 (Conflict)
          alert("User already exists");
        } else {
          // Otros errores
          alert(error);
        }
      });
  };

 
  

  return (
    <div className={css.container}>
      <form onSubmit={handleSubmit} className={css.form}>
        <div className={css.signin}>
          <h1>Signup</h1>
        </div>
        <input
          type="user"
          placeholder="Insert username"
          name="user"
          className={css.input}
        />
        <input
          type="email"
          placeholder="Insert email"
          name="email"
          className={css.input}
        />
        <input
          type="password"
          placeholder="Insert password"
          name="password"
          className={css.input}
        />
        <button type="submit" className={css.btn}>
          Sign Up
        </button>
      </form>
    </div>
  );
}
