import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import css from "./list.module.css";
import { addList } from "../../REDUX/Actions/addList.js";

export default function List() {
  const dispatch = useDispatch();
  const list = useSelector((state) => state.listado);
  console.log(list);

  const [item, setItem] = useState("");

  function handleInputChange(e) {
    e.preventDefault();
    setItem(e.target.value);
  }

  function handleClick(e) {
    e.preventDefault();
    dispatch(addList(item));
    setItem("");
  }

  function handleCheckboxChange(index) {
    const updatedList = list.map((item, i) => {
      if (i === index) {
        return {
          ...item,
          checked: !item.checked, // Invierte el valor de la propiedad 'checked'
        };
      }
      return item;
    });
  
    // Dispatch an action or update the state with the updated list
  }
  

  return (
    <div className={css.container}>
      <div className={css.animacion1}>
        <iframe src="https://embed.lottiefiles.com/animation/68373" className={css.anim1}></iframe>
      </div>

      <div className={css.list}>
        <div className={css.titulo}>
          <h1>To Do</h1>
        </div>
        <div>
          <input
            type="text"
            placeholder="Comprar tomates..."
            className={css.input}
            value={item}
            onChange={(e) => handleInputChange(e)}
          />
          <button onClick={(e) => handleClick(e)}>Agregar</button>
        </div>
        <div className={css.listado}>
          {list.map((item, index) => (
            <div key={index} className={css.ul}>
              <div className={css.checkBox}>
                <input type="checkbox" onChange={() => handleCheckboxChange(index)} />
              </div>
              <div className={item.checked ? css.itemChecked : css.item}>
                {item.text}
              </div>
              <div className={css.x}>‚ùå</div>
            </div>
          ))}
        </div>
      </div>

      <div className={css.animacion2}>
        <iframe src="https://embed.lottiefiles.com/animation/140959" className={css.anim2}></iframe>
      </div>
    </div>
  );
}
