const initialState = {
  listado: ["comprar mantel"],
};

function rootReducer(state = initialState, action) {
  switch (action.payload) {
    case "GET_LIST":
      return {
        ...state,
        listado: action.payload,
      };

    case "ADD_LIST":
      
      return {
        ...state,
        listado: [...state.listado, action.payload],
      };

    default:
      return {
        ...state,
      };
  }
}

export default rootReducer;
