import axios from "axios";

const createUser = (userData) => {
  return new Promise((resolve, reject) => {
    axios
      .post("http://localhost:3001/signup", userData)
      .then((response) => {
        // Usuario creado exitosamente
        // Aquí puedes mostrar una alerta o realizar otras acciones
        resolve(response.data); // Resuelve la promesa con los datos de respuesta
      })
      .catch((error) => {
        // Error en la creación del usuario
        if (error.response && error.response.status === 409) {
          // Si el estado de respuesta es 409 (Conflict)
          // Aquí puedes mostrar una alerta o realizar otras acciones
          reject("User already exists"); // Rechaza la promesa con un mensaje de error
        } else {
          // Otros errores
          // Aquí puedes manejar otros errores de la solicitud
          reject("Error: " + error); // Rechaza la promesa con un mensaje de error
        }
      });
  });
};

export default createUser;
