import React, { createContext, useState, useContext} from 'react';

// Crea el contexto de usuario
export const UserContext = createContext();

export const useAuth = () => {
    const context = useContext(UserContext)
}


// Crea el componente proveedor que almacenará y proporcionará los datos del usuario
export const UserProvider = ({ children }) => {

  const [user, setUser] = useState(null);

  // Función para establecer los datos del usuario una vez que se haya autenticado
  const login = (userData) => {
    setUser(userData);
  };

  // Función para eliminar los datos del usuario al cerrar sesión
  const logout = () => {
    setUser(null);
  };

  // Proporciona el estado 'user', las funciones 'login' y 'logout' a través del contexto
  return (
    <UserContext.Provider value={{ user, login, logout }}>
      {children}
    </UserContext.Provider>
  );
};

// Exporta el contexto de usuario
export default UserContext;
