import React, { useState, useEffect } from "react";
import css from "./navbar.module.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser, faListCheck } from "@fortawesome/free-solid-svg-icons";
import Modal from "react-modal";
import ModalLogin from "../ModalLogin/ModalLogin.jsx";
import ModalSignup from "../ModalSignup/ModalSignup.jsx";

export default function Navbar() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isUserCreated, setIsUserCreated] = useState({
    value: false,
    isLoading: false
  });

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  useEffect(() => {
    if (isUserCreated.value) {
      setIsModalOpen(false);
    }
  }, [isUserCreated.value]);

  return (
    <div className={css.container}>
      <div className={css.logo}>
        <FontAwesomeIcon
          icon={faListCheck}
          style={{ color: "#e5ea47" }}
          className={css.task}
        />
        <h1>TaskTracker</h1>
      </div>
      <div className={css.usuario}>
        <FontAwesomeIcon
          icon={faUser}
          style={{ color: "#ffffff" }}
          className={css.user}
          onClick={toggleMenu}
        />
        {isMenuOpen && (
          <div className={css.menu}>
            <a href="#" onClick={toggleModal} className={css.L}>
              LogIn
            </a>
            <Modal
              isOpen={isModalOpen}
              onRequestClose={toggleModal}
              contentLabel="Login Modal"
            >
              <ModalLogin />
            </Modal>
            <a href="#" onClick={toggleModal} className={css.S}>
              SignUp
            </a>
            <Modal
              isOpen={isModalOpen}
              onRequestClose={toggleModal}
              contentLabel="Signin Modal"
            >
              <ModalSignup
                onUserCreated={setIsUserCreated}
                toggleModal={toggleModal}
              />
            </Modal>
          </div>
        )}
      </div>
    </div>
  );
}
