import React from "react";
import css from "./ModalSignup.module.css"
import { useDispatch } from "react-redux";
import createUser from "../../REDUX/Actions/createUser.js"

export default function ModalSignup({ toggleModal2 }) {
  const dispatch = useDispatch()

  const handleSubmit = (e) => {
    e.preventDefault();
    // Aquí puedes manejar la lógica de envío del formulario
    const { user, email, password } = e.target
    const userData = {
      user: user.value,
      email: email.value,
      password:password.value
    }
    dispatch(createUser(userData))
  };

  return (
    <div className={css.container}>
      
      <form onSubmit={handleSubmit} className={css.form}>
        <div className={css.signin}>
          <h1>Signup</h1>
        </div>
        <input type="user" placeholder=" Insert username" name="user" className={css.input} />
        <input type="email" placeholder=" Insert email" name="email" className={css.input} />
        <input type="password" placeholder=" Insert password" name="password" className={css.input}/>
        <button type="submit" className={css.btn}>Iniciar sesión</button>
    </form>
    </div>
    
  );
}
