import React, { useEffect, useState , useRef} from "react"
import { useDispatch, useSelector } from "react-redux"
import css from "./list.module.css";
import { addList } from "../../REDUX/Actions/addList.js";

export default function List() {

  const dispatch = useDispatch()
  const List = useSelector((state) => state.listado)
  console.log(List)

  const [Item , setItem] = useState("")

  

  function handleInputChange(e) {
    e.preventDefault();
    setItem(e.target.value);
  }

  function handleClick(e) {
    e.preventDefault();
    dispatch(addList(Item))
    setItem("")
  }

  return (
    <div className={css.container}>

      <div className={css.animacion1}> 
      <iframe src="https://embed.lottiefiles.com/animation/68373" className={css.anim1}></iframe>
      </div>

      <div className={css.list}>
        <div className={ css.titulo}>
          <h1>To Do</h1>
        </div>
        <div >
          <input type="text" className={css.input} value={Item} onChange={(e) => handleInputChange(e)}/>
          <button onClick={(e) => handleClick(e)}>Agregar</button>
        </div>
        <div className={ css.listado}>
            {List.map((item, index) => (
              <div key={index} className={css.ul}>
                 <div className={css.checkBox}>
                   <input type="checkbox" />
            </div>
            <div className={css.item}>
              {item}
            </div>
            <div className={css.x}>
              ‚ùå
            </div>
          </div>
        ))}
          
        </div>
      </div>

      <div className={css.animacion2}> 
        <iframe src="https://embed.lottiefiles.com/animation/140959" className={css.anim2}></iframe>
      </div>

    </div>
  )
}
