import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import css from "./ModalSignup.module.css";
import { useDispatch } from "react-redux";
import createUser from "../../REDUX/Actions/createUser.js";

export default function ModalSignup() {
  const dispatch = useDispatch();
  const history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();

    const { user, email, password } = e.target;
    const userData = {
      user: user.value,
      email: email.value,
      password: password.value
    };

    if (userData.user === "" || userData.email === "" || userData.password === "") {
      return alert("You must complete all fields");
    }

    dispatch(createUser(userData))
      .then((data) => {
        alert("User created successfully");
        history.push("/home"); // Redirecciona al usuario a la página de inicio
      })
      .catch((error) => {
        alert(error.message);
      });
  };

  return (
    <div className={css.container}>
      <form onSubmit={handleSubmit} className={css.form}>
        <div className={css.signin}>
          <h1>Signup</h1>
        </div>
        <input
          type="user"
          placeholder="Insert username"
          name="user"
          className={css.input}
        />
        <input
          type="email"
          placeholder="Insert email"
          name="email"
          className={css.input}
        />
        <input
          type="password"
          placeholder="Insert password"
          name="password"
          className={css.input}
        />
        <button type="submit" className={css.btn}>
          Sign Up
        </button>
      </form>
    </div>
  );
}
