import axios from "axios";

export function getTasks() {
  return async function (dispatch) {
    const tasks = await axios.get("http://localhost:3001/tasks");
    return dispatch({
      type: "GET_TASKS",
      payload: tasks.data,
    });
  };
}

export default createUser;
